const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const o=e.getAttribute("href").substr(1);document.getElementById(o).scrollIntoView({behavior:"smooth",block:"start"})}));window.addEventListener("scroll",(()=>{let e=window.pageYOffset,t=document.querySelector(".header");e>=100?t.classList.add("bg-header"):t.classList.remove("bg-header")}));