const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const c=e.getAttribute("href").substr(1);document.getElementById(c).scrollIntoView({behavior:"smooth",block:"start"})}));window.addEventListener("scroll",(()=>{let e=window.pageYOffset,t=document.querySelector(".header");e>=100?t.classList.add("bg-header"):t.classList.remove("bg-header")}));const modalAuth=document.querySelector(".modal-auth"),formAuth=document.querySelector(".form-auth");document.querySelector(".header__login").addEventListener("click",(()=>{modalAuth.classList.add("modal-auth-active")})),modalAuth.addEventListener("click",(function(){modalAuth.classList.remove("modal-auth-active")})),formAuth.addEventListener("click",(function(e){e.stopPropagation()}));const alertInfo=()=>{const e=document.querySelector(".alert-info"),t=document.querySelector("#close-alert");setTimeout((()=>{e.classList.add("alert-info-active")}),1500),t.addEventListener("click",(function(){e.classList.remove("alert-info-active")}))};function slider(){const e=document.querySelector(".slider__items"),t=document.querySelectorAll(".control__item");let c=0;function o(e){switch(e){case 0:t[0].classList.add("control__item-active"),t[1].classList.remove("control__item-active"),t[2].classList.remove("control__item-active");break;case-100:t[0].classList.remove("control__item-active"),t[1].classList.add("control__item-active"),t[2].classList.remove("control__item-active");break;case-200:t[0].classList.remove("control__item-active"),t[1].classList.remove("control__item-active"),t[2].classList.add("control__item-active")}}setInterval((()=>{c-=100,c<-200&&(c=0),e.style.left=c+"%",o(c)}),4e3);t[0].addEventListener("click",(()=>{c=0,e.style.left=c+"%",o(c)})),t[1].addEventListener("click",(()=>{c=-100,e.style.left=c+"%",o(c)})),t[2].addEventListener("click",(()=>{c=-200,e.style.left=c+"%",o(c)}))}alertInfo(),slider();